import React from "react"
class Modal extends React.Component{
    constructor(props){
        super(props)
        this.state = { x:0, y:0 };
    }
    _onMouseMove(e) {
        this.setState({ x: e.nativeEvent.offsetX, y: e.nativeEvent.offsetY });
    }

}

