import { useState,useEffect } from "react";
import axios from "axios"

const Modal = ({ data }) => {
    const [coord1, setCoord1] = useState(null)
    const [coord2, setCoord2] = useState(null)
    const [counter, setCounter] = useState(0)
    function GetCoiders(){
        const handleClick  = (e) => {

            if(counter !== 2){
                if((coord1 === null) && (coord2 === null)){
                    setCounter(0)
                }
                if(counter === 0){
                    setCoord1({ x:e.pageX, y:e.pageY });
                    setCounter(counter + 1)
                }
                if(counter === 1){
                    setCoord2({ x2:e.pageX, y2:e.pageY });
                    setCounter(counter + 1)
                }
            }
            getPost(coord1, coord2)   
        }
        return(
            <img onClick={handleClick} src={data.path} alt={data.name} />
        )
    }

    function getPost(cordenate1, cordenate2){
        console.log(cordenate1,cordenate2)
    }
    

    return(
       <GetCoiders />
    )
}

export default Modal;
