import { useState,useEffect } from "react";
import axios from "axios"

const Modal = ({ data }) => {
    const [coord, setCoord] = useState(null)
    const [post, setPost] = useState(null);

    const handleMouseMove = (e) => {
        setCoord({ x: e.pageX, y: e.pageY });
        getCordinates(e.pageX,e.pageY)
    };
    async function getCordinates(x, y){
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ setX: x, sety: y })
        };
        fetch('http://localhost:5000/newmage', requestOptions )
        .then(response => response.json())
        .then(data => setState({ postId: data.id }));
    }
    
    return(
        <img onClick={handleMouseMove} src={data.path} alt={data.name} />
        
    )
}

export default Modal;
