import { useState,useEffect } from "react";
import axios from "axios"

const Modal = ({ data }) => {

    const handleClick  = (e) => {
        const [coord1, setCoord1] = useState(null)
        const [coord2, setCoord2] = useState(null)
        const [counter, setCounter] = useState(0)
        if((coord1 && coord2) === null){
            if(counter !== 2){
                if(counter === 0){
                    console.log(counter)
                    setCoord1({ x1: e.pageX, y1: e.pageY }); 
                    setCounter(counter + 1)

                }else{
                    console.log(counter)
                    setCoord2({ x2: e.pageX, y2: e.pageY }); 
                    setCounter(counter + 1)
                }
            }else {
                if(coord1 === coord2){
                    setCounter(0);
                    alert('давайте заного')
                }
            }
        }
        postCordinates(coord1,coord2)
    };
        
    
    function postCordinates(coord1,coord2){
        console.log(coord1,coord2)
    }

    return(
        // <button onClick={handleClick}>Click me</button>
        <img onClick={handleClick} src={data.path} alt={data.name} />
        
    )
}

export default Modal;
