import { useState,useEffect } from "react";
import axios from "axios"

const Modal = ({ data }) => {
    const [coord1, setCoord1] = useState(null)
    const [coord2, setCoord2] = useState(null)
    const [counter, setCounter] = useState(1)

    const handleClick  = (e) => {
        if((coord1 && coord2) === null){
            if(counter !== 2){
                if(counter === 1){
                    setCoord1({ x: e.pageX, y: e.pageY }); 
                    setCounter(counter + 1)
                }else{
                    setCoord2({ x: e.pageX, y: e.pageY }); 
                    setCounter(counter + 1)
                }
            }else{
                if(coord1 === coord2){
                    setCounter(0);
                    alert('давайте заного')
                }
                alert('123')
            }
        }

    };


    return(
        // <button onClick={handleClick}>Click me</button>
        <img onClick={handleClick} src={data.path} alt={data.name} />
        
    )
}

export default Modal;
