    const handleMouseMove = (e) => {
        setCoord({ x: e.pageX, y: e.pageY });
        getCordinates(e.pageX,e.pageY)
    };
    async function getCordinates(x, y){
        fetch('http://localhost:5001/', { 
            // new part:
            method: 'post',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify({
              favorites: this.state.favorites})
            // old part - worked before i tried to send data to the backend:
          })
          .then((response) => response.json())
          .then(stockList => {
              this.setState({ stocks: stockList });
                  console.log(stockList);
          });
    }
    
    return(
        <img onClick={handleMouseMove} src={data.path} alt={data.name} />
        
    )
}

export default Modal;
