import { useState } from "react"

const Modal = ({ data }) => {
    const img = document.querySelector("img")
    img.onclick = (e) => {
        const x = e.pageX - e.target.offsetLeft;
        const y = e.pageY - e.target.offsetTop;
        console.log(x, y);
    }
    if(data === ""){
        return(
            <div>
                <img  src={data.path} alt={data.name} />
            </div>
        )
    }
    
}

export default Modal;
